import { solid } from "@fortawesome/fontawesome-svg-core/import.macro";
import { useEffect, useState } from "react";

function Star({ star }) {
    const [nbStar, setNbStar] = useState(null)
  const DisplayStar = () => {
    let arrayStar = []
    for(let i = 0; i<star; i++){
        arrayStar.push("star")
    }
    if(star < 5){
        let noStar = 5-star
        for(let i = 0; i<noStar; i++){
            arrayStar.push("noStar")
        }
    }
    setNbStar(null)
  };

  useEffect(() => {
    DisplayStar()
  })

  return (
    <div>
        <DisplayStar />
    </div>
  );
}

export default Star;
