import kasa from "../json/kasa.json";
import { Link } from "react-router-dom";
import "../App.css"

function Article() {
  return (
    <div
      className="article"
      style={{
        display: "flex",
        flexDirection: "row",
        flexWrap: "wrap",
        justifyContent: "space-around",
        marginTop: 40,
        backgroundColor: "#F6F6F6",
        borderRadius: 25,
        padding: "40px 0",
      }}
    >
      {kasad.map((item, index) => (
        <Link to={`/article?id=${item.id}`}
          key={item.id}
          style={{
            width: 340,
            height: 340,
            margin: "20px 20px",
            borderRadius: 10,
            textDecoration: 'none'
          }}
        >
          <img
            style={{
              width: 340,
              height: 340,
              objectFit: "cover",
              borderRadius: 10,
              position: "absolute",
            }}
            src={item.cover}
            alt={item.title}
          />
          <div
            style={{
              width: 340,
              height: 340,
              borderRadius: 10,
              background: "linear-gradient(to top, black, transparent)",
              position: "absolute",
              opacity: 0.5,
            }}
          ></div>
          <span style={{
              width: 200,
              height: 340,
              borderRadius: 10,
              position: "relative",
              display:"flex",
              alignItems:"flex-end",
              color:"white",
              bottom:20,
              left:20
            }}>{item.title}</span>
        </Link>
      ))}
    </div>
  );
}

export default Article;
