import { useEffect, useState } from "react";
import kasa from "../../json/kasa.json";

function SlugArticle() {
  const [getUrl, setGetUrl] = useState(null);
  const [data, setData] = useState(null);
  useEffect(() => {
    const get = window.location.href;
    const url = get.split("=")[1];
    const searchArticle = kasa.filter((item) => item.id === url);
    setGetUrl(url);
    setData(searchArticle[0]);
  }, []);
  if (getUrl != null && data != null) {
    return (
      <main>

        <div style={{display:"flex", flexDirection:"row", justifyContent:"space-between"}}>
          <div style={{display:"flex", flexDirection:"column", justifyContent:"center"}}>
            <h1 style={{margin:0, marginBottom:10, color:"#FF6060"}}>{data.title}</h1>
            <p style={{margin:0, color:"#FF6060"}}>{data.location}</p>
          </div>
          <div style={{flexDirection:"row", display:"flex", alignItems:"center"}}>
            <div style={{display:"flex", flexDirection:"column", textAlign:"right", marginRight:10}}>
              <span style={{color:"#FF6060"}}>{data.host.name.split(" ")[0]}</span>
              <span style={{color:"#FF6060"}}>{data.host.name.split(" ")[1]}</span>
            </div>
            <img src={data.host.picture} style={{width:50, height:50, borderRadius:100, objectFit:"contain"}} alt={`profil de ${data.host.name}`}/>
          </div>
        </div>

        <div style={{display:'flex', flexDirection:"row"}}>
          {data.tags.map((item,index) => (
            <span style={{padding:"2px 20px", backgroundColor:"#FF6060", marginRight:20, borderRadius:100, color:"white"}}>{item}</span>
          ))}
        </div>
      </main>
    );
  } else {
    return null;
  }
}

export default SlugArticle;
