import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid } from '@fortawesome/fontawesome-svg-core/import.macro'

function Accordeons({ title, content }) {
  return (
    <div style={{ display: "flex", flexDirection: "column"}}>
      <div
        style={{
          backgroundColor: "#FF6060",
          padding: "10px 10px",
          borderRadius: 10,
          color: "white",
          position: "relative",
          zIndex: 10,
          display:"flex",
          flexDirection:"row",
          justifyContent:"space-between"
        }}
      >
        <span>{title}</span>
        <FontAwesomeIcon icon={solid('chevron-down')} onClick={() => console.log("bonjour")}/>
      </div>
      <span
        style={{
          backgroundColor: "#F6F6F6",
          padding: "10px 10px",
          borderBottomLeftRadius: 10,
          color: "#FF6060",
          borderBottomRightRadius: 20,
          position: "relative",
          top: -10,
          paddingTop: 20,
          display:"none"
        }}
      >
        {content}
      </span>
    </div>
  );
}

export default Accordeons;
