import kasa from "../json/kasa.json";
import { Link } from "react-router-dom";
import "../assets/css/Article.css"

function Article() {
  return (
    <div
      className="article"
    >
      {kasa.map((item, index) => (
        <Link
          to={`/article?id=${item.id}`}
          key={item.id}
          className={"article_link"}
        >
          <img
            style={{
              width: 340,
              height: 340,
              objectFit: "cover",
              borderRadius: 10,
              position: "absolute",
            }}
            src={item.cover}
            alt={item.title}
          />
          <div
            style={{
              width: 340,
              height: 340,
              borderRadius: 10,
              background: "linear-gradient(to top, black, transparent)",
              position: "absolute",
              opacity: 0.5,
            }}
          ></div>
          <span
            style={{
              width: 200,
              height: 340,
              borderRadius: 10,
              position: "relative",
              display: "flex",
              alignItems: "flex-end",
              color: "white",
              bottom: 20,
              left: 20,
            }}
          >
            {item.title}
          </span>
        </Link>
      ))}
    </div>
  );
}

export default Article;
